"""    """import pandas as pdfrom data_organizing import df_customizerleague_info = pd.read_csv('league_info.csv', index_col='Country')countries = league_info.indexyears = range(2005,2023)tiers = [1,2]merged_tables = df_customizer(countries,years,tiers)# Alternatively, load the merged_tables using:# merged_tables = pd.read_csv(#     'merged_tables.csv',#     header=[0, 1, 2],#     index_col=[0,1],#     skipinitialspace=True#     )final_dataset = pd.DataFrame()columns = merged_tables[years[0]][f'Tier {tiers[0]}'].columnsl_columns = ['l-'+col for col in columns]l_columns.extend(columns)columns = l_columnsyears = range(2006,2023)for year in years:    df_year = pd.concat(        [merged_tables.loc[countries,year-1]['Tier 1'],         merged_tables.loc[countries,year]['Tier 1']         ], axis=1).copy()    df_year.columns = columns    final_dataset = pd.concat([final_dataset,df_year],axis=0)final_dataset = final_dataset.dropna(how='all', axis=0)final_dataset.to_csv('final_dataset.csv')